import React, { Component, useState } from 'react';
import ReactPlayer from 'react-player/lazy'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'

class Video extends Component {
  constructor(props) {
    super(props)

    //var show = false;
    this.state = {
      card: {
        buttonPressed: true
      }

    }
  }



  setShow = (val) => {
    const [show, setShow] = useState(val);
  }
  render() {
    return (
      <>
        <Button variant="primary" onClick={() => this.setShow(true)}>
          Custom Width Modal
      </Button>

        <Modal
          show={this.show}

        >
          <Modal.Header closeButton>
            <Modal.Title id="example-custom-modal-styling-title">
              Custom Modal Styling
          </Modal.Title>
          </Modal.Header>
          <Modal.Body>

          </Modal.Body>
        </Modal>
      </>
    );};
}

//export default video; 
class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      card:{
        buttonPressed:true
      }

    }
  } 

  videoPlayer = () => {
    var current = this.state.card.buttonPressed;

    if(current === true){
      this.setState({ card: { buttonPressed: false } });
    } else if (current === false) {
      this.setState({ card: { buttonPressed: true } });
    }

  }

  render()
  {
    return (
    <div>
      <button onClick={this.videoPlayer}>Play</button>
      <div >
        {this.state.card.buttonPressed ? <ReactPlayer url='https://www.youtube.com/watch?v=IUN664s7N-c' controls playing /> : ""}
      </div>
    </div>
    )
  }
}

//export {App, Video};
export {App, Video};

