import React, { Component} from 'react';
import ReactPlayer from 'react-player/lazy'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'

class Video extends Component {
  constructor(props) {
    super(props)

    //var show = false;
    this.state = {
      show:false
      }

    }


  render(){
    return (
      <div>
        <Button className = "Button"    onClick={() => this.setState({show:true})}>
         Play
        </Button> 

        <Modal
          show={this.state.show}
          onHide={() => this.setState({ show: false })}
          size = 'lg'
          centered
        >
          <ReactPlayer url='https://www.youtube.com/watch?v=IUN664s7N-c' controls playing  width='100' height='420px' />
        </Modal>
      </div>
    );
  }
}

class NavBar extends Component {
  constructor(props) {
    super(props)

    //var show = false;
    this.state = {
      show: false
    }

  }


  render() {
    return (
      <div>
        <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
          <Navbar.Brand href="#home">Elec</Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="#features">Features</Nav.Link>
              <Nav.Link href="#pricing">Pricing</Nav.Link>

            </Nav>
            <Nav>
              <Nav.Link href="#deets">More deets</Nav.Link>
              <Nav.Link eventKey={2} href="#memes">
                Dank memes
      </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </div>
    );
  }
}

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      card:{
        buttonPressed:true
      }

    }
  } 

  videoPlayer = () => {
    var current = this.state.card.buttonPressed;

    if(current === true){
      this.setState({ card: { buttonPressed: false } });
    } else if (current === false) {
      this.setState({ card: { buttonPressed: true } });
    }

  }

  render()
  {
    return (      
      <div >
        <Nav/>
        <br></br>
        {this.state.card.buttonPressed ? <Video/> : ""}

      </div>
    )
  }
}

//export {App, Video};
export {App, Video};

