import React, { Component } from 'react';
import ReactPlayer from 'react-player/lazy'


class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      card:{
        buttonPressed:'block'
      }

    }
    //this.videoPlayer = 
  } 

  videoPlayer = () => {
    current = this.state.card.buttonPressed
    if(current == ' block'){
      this.setState({ card: { buttonPressed: 'none' } })
    } else if (current == ' none') {
      this.setState({ card: { buttonPressed: 'block' } })
    }

    //return '1'
    //return (<ReactPlayer url='https://www.youtube.com/watch?v=IUN664s7N-c' controls light playing pip />)
  }
//
  render()
  {
    return (
    <div>
      <button onClick={this.videoPlayer}>Play</button>
      <div style={{display:this.state.card.buttonPressed}}>
          {this.state.card.buttonPressed ? <ReactPlayer url='https://www.youtube.com/watch?v=IUN664s7N-c' controls light playing pip /> : ""}
      </div>
    </div>
    )

  }

}

export default App;
