import React, { Component} from 'react';
import ReactPlayer from 'react-player/lazy'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'

class Video extends Component {
  constructor(props) {
    super(props)

    //var show = false;
    this.state = {
      show:false
      }

    }


  render(){
    return (
      <div>
        <Button variant="primary" onClick={() => this.setState({show:true})}>
         Play
      </Button>

        <Modal
          show={this.state.show}
          onHide={() => this.setState({ show: false })}
          dialogClassName="modal-90w"
          aria-labelledby="example-custom-modal-styling-title"

          centered
        >
          <Modal.Header closeButton>
            <Modal.Title>Modal title</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <ReactPlayer url='https://www.youtube.com/watch?v=IUN664s7N-c' controls playing pip />
        </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => this.setState({ show: false })}>
              Close
          </Button>
            <Button variant="primary">Understood</Button>
          </Modal.Footer>

        </Modal>
      </div>
    );
  }
}

//export default video; 
class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      card:{
        buttonPressed:true
      }

    }
  } 

  videoPlayer = () => {
    var current = this.state.card.buttonPressed;

    if(current === true){
      this.setState({ card: { buttonPressed: false } });
    } else if (current === false) {
      this.setState({ card: { buttonPressed: true } });
    }

  }

  render()
  {
    return (      
      <div >
        {this.state.card.buttonPressed ? <Video/> : ""}
      </div>
    )
  }
}

//export {App, Video};
export {App, Video};

