import React, { Component } from 'react';
import ReactPlayer from 'react-player/lazy'
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap'

class Video extends Component {
  constructor(props) {
    super(props)
    this.state = {
      card: {
        buttonPressed: true
      }

    }
  }


  render() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Launch demo modal
      </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
          </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
          </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}

//export default video; 
class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      card:{
        buttonPressed:true
      }

    }
  } 

  videoPlayer = () => {
    var current = this.state.card.buttonPressed;

    if(current === true){
      this.setState({ card: { buttonPressed: false } });
    } else if (current === false) {
      this.setState({ card: { buttonPressed: true } });
    }

  }

  render()
  {
    return (
    <div>
      <button onClick={this.videoPlayer}>Play</button>
      <div >
        {this.state.card.buttonPressed ? <ReactPlayer url='https://www.youtube.com/watch?v=IUN664s7N-c' controls playing /> : ""}
      </div>
    </div>
    )
  }
}

//export {App, Video};
export {App, Video};

